INSERT INTO `jos_categories` VALUES ('15', '0', 'Cultura', 'Cultura', '', 'com_weblinks', 'left', '<p>\r\nGrupo incentiva o estudo filos&oacute;fico em Joinville<br />\r\n<br />\r\n<br />\r\nTodos os domingos um grupo, entre 10 e 20 pessoas, re&uacute;ne-se na&nbsp; &nbsp;Megastore da Livrarias <br />\r\nCuritiba em Joinville para estudar e debater&nbsp; &nbsp;conhecimentos de obras filos&oacute;ficas <br />\r\ndispon&iacute;veis no acervo da loja.<br />\r\n<br />\r\n<br />\r\nO projeto &eacute; aberto &agrave; participa&ccedil;&atilde;o do p&uacute;blico em geral, coordenado&nbsp; &nbsp;por profissionais <br />\r\ncapacitados, no qual s&atilde;o lidos, estudados e&nbsp; &nbsp;debatidos textos de fil&oacute;sofos, construindo <br />\r\num paralelo com a&nbsp; &nbsp;sociedade atual.<br />\r\n<br />\r\n<br />\r\n<br />\r\nServi&ccedil;o:\r\n</p>\r\n<p>\r\n<br />\r\nGrupo de Estudos de Filosofia<br />\r\n<br />\r\nLocal: Livrarias Curitiba - Shopping Muller - Rua Pedro Lobo - Centro<br />\r\n<br />\r\nQuando: todos os domingos<br />\r\n<br />\r\nHor&aacute;rio: 18 &agrave;s 19 horas<br />\r\n<br />\r\nEntrada gratuita<br />\r\n<br />\r\nContato: (47) 3433.6400\r\n</p>\r\n', '1', '0', '0000-00-00 00:00:00', NULL, '13', '0', '0', '');
