INSERT INTO `jos_categories` VALUES ('33', '0', 'APOIO COMUNICAÇÃO', 'APOIO COMUNICAÇÃO', '', 'com_weblinks', 'left', '<strong><span style=\"font-size: 18pt; color: #ff0000; font-family: Verdana\">SUGEST&Atilde;O DE PAUTA:</span></strong> \r\n<div>\r\n<span style=\"font-size: 10pt; color: #003366; font-family: Verdana\"></span>\r\n</div>\r\n<div>\r\n<span style=\"font-size: 10pt; color: #003366; font-family: Verdana\">\r\n<p align=\"center\" style=\"margin: 0cm 0cm 0pt\" class=\"MsoHeading7\">\r\n<span style=\"color: windowtext\"><strong><span style=\"font-size: 18pt\"><span style=\"font-family: Arial\">McDonald&#146;s promove concurso cultural para&nbsp;crian&ccedil;as</span></span></strong></span>\r\n</p>\r\n<span style=\"font-size: 14pt; font-family: Arial\"><o:p><strong>&nbsp;</strong></o:p></span><span style=\"font-family: Arial\"><o:p>&nbsp;</o:p></span> \r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify\" class=\"MsoNormal\">\r\n<span style=\"font-family: Arial\">Crian&ccedil;as de todo o Brasil poder&atilde;o explorar a m&uacute;sica em suas mais variadas formas na campanha &#147;Meu filho &eacute; um pop star&#148; do McDonald&#146;s, </span><span style=\"font-family: Arial\">para crian&ccedil;as de at&eacute; 12 anos. Para participar, os respons&aacute;veis devem tirar uma foto engra&ccedil;ada, criativa ou divertida das crian&ccedil;as, com um dos instrumentos surpresa da campanha McLanche Feliz deste m&ecirc;s, fazer&nbsp;a inscri&ccedil;&atilde;o e enviar a imagem pelo link da promo&ccedil;&atilde;o no site </span><a href=\"http://www.mcdonalds.com.br/\"><span style=\"font-family: Arial\"><span style=\"color: #0000ff\">www.mcdonalds.com.br</span></span></a><span style=\"font-family: Arial\">, onde consta o regulamento. As seis crian&ccedil;as escolhidas em cada uma das categorias das surpresas gravar&atilde;o um videoclipe com Ronald McDonald. </span>\r\n</p>\r\n<strong><span style=\"color: black; font-family: Arial\"><o:p>&nbsp;</o:p></span></strong> \r\n<p style=\"margin: 0cm 0cm 0pt\" class=\"MsoNormal\">\r\n<strong><span style=\"color: black; font-family: Arial\">McDonald\'s Brasil</span></strong>\r\n</p>\r\n<p style=\"margin: 0cm 0cm 0pt\" class=\"MsoBodyTextIndent\">\r\n<span style=\"font-family: Arial\">O McDonald\'s &eacute; l&iacute;der no segmento de alimenta&ccedil;&atilde;o fora de casa e conta com 562&nbsp;restaurantes e 53 McCaf&eacute; no&nbsp;Brasil. H&aacute; 29 anos atuando no pa&iacute;s, a rede est&aacute; presente em mais de 170 cidades, localizadas em 21 estados e no Distrito Federal. Atende diariamente 1,6 milh&atilde;o de clientes e ocupa a oitava posi&ccedil;&atilde;o em vendas no ranking de pa&iacute;ses da corpora&ccedil;&atilde;o.&nbsp;O McDonald\'s &eacute; um dos maiores empregadores privados do Brasil, com mais de 38<span style=\"font-size: 10pt; color: blue\">&nbsp;</span>mil funcion&aacute;rios, e uma das empresas que oferece o maior n&uacute;mero de oportunidades de primeiro emprego<span style=\"font-size: 10pt\">.</span><span style=\"font-size: 10pt; color: blue\">&nbsp;</span></span><span style=\"font-size: 10pt\"></span>\r\n</p>\r\n&nbsp;</span>\r\n</div>\r\n<div class=\"Section1\">\r\n<span style=\"font-size: 10pt; color: #003366; font-family: Verdana\"><o:p>&nbsp;</o:p></span><span style=\"font-size: 10pt; color: #003366; font-family: Verdana\"><o:p>&nbsp;</o:p></span> \r\n<p class=\"MsoNormal\">\r\n<span style=\"font-size: 10pt; color: #003366; font-family: Verdana\">Adriana Laffin - Jornalista</span>\r\n</p>\r\n</div>\r\n', '1', '65', '2009-03-25 16:29:40', NULL, '1', '0', '0', '');
