INSERT INTO `jos_content` VALUES ('36', 'Quem Somos', 'Quem Somos', '<p align=\"justify\">\r\n<span style=\"font-size: 12pt; line-height: 115%; font-family: \'Arial\',\'sans-serif\'\"><span style=\"color: #000000\">O site aquitemjoinville &eacute; uma empresa de m&iacute;dia fundada no dia 12 de dezembro de 2008. Oferecemos servi&ccedil;os gratuitos de busca r&aacute;pida de empresas, com&eacute;rcios, ind&uacute;strias e servi&ccedil;os para a popula&ccedil;&atilde;o de Joinville e regi&atilde;o, de forma clara e objetiva facilitando o dia-a-dia de todos.</span></span>&nbsp; \r\n</p>\r\n<p align=\"justify\">\r\n<span style=\"font-size: 12pt; line-height: 115%; font-family: \'Arial\',\'sans-serif\'\"><span style=\"color: #000000\">Criando </span></span><span style=\"font-size: 12pt; line-height: 115%; font-family: \'Arial\',\'sans-serif\'\"><span style=\"color: #000000\">a oportunidade de crescimento aos nossos clientes na forma de divulga&ccedil;&atilde;o de seus produtos e servi&ccedil;os.</span></span> \r\n</p>\r\n<p align=\"justify\">\r\n<span style=\"font-size: 12pt; line-height: 115%; font-family: \'Arial\',\'sans-serif\'\"><span style=\"color: #000000\">Uma empresa de Joinville para joinvilenses que percebendo o constante uso da Internet encontramos uma forma de ajudar o povo de nossa cidade.&nbsp;</span></span><span style=\"font-size: 12pt; line-height: 115%; font-family: \'Arial\',\'sans-serif\'\"><span style=\"color: #000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-size: 12pt; line-height: 115%; font-family: \'Arial\',\'sans-serif\'\"><span style=\"color: #000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span> \r\n</p>\r\n<p>\r\n<span style=\"font-family: \'Arial\',\'sans-serif\'\"><span style=\"font-size: 12pt\"><span style=\"color: #000000\">Para o futuro vislumbramos ainda mais trabalho, agregando tecnologia e informa&ccedil;&otilde;es para que possamos oferecer sempre o que h&aacute; de melhor &agrave; popula&ccedil;&atilde;o joinvilense.</span></span></span><span style=\"font-family: \'Arial\',\'sans-serif\'\"><span style=\"font-size: 12pt\"><span style=\"color: #000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></span><span style=\"font-family: \'Arial\',\'sans-serif\'\"><span style=\"font-size: 12pt\"><span style=\"color: #000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></span> \r\n</p>\r\n<span style=\"font-family: \'Arial\',\'sans-serif\'\"><span style=\"font-size: 12pt\"><span style=\"color: #000000\">Trabalhamos sempre na busca da satisfa&ccedil;&atilde;o de nossos clientes e parceiros mantendo uma rela&ccedil;&atilde;o de confian&ccedil;a e seriedade.<o:p></o:p></span></span></span><span style=\"font-family: \'Arial\',\'sans-serif\'\"><o:p><span style=\"font-size: 12pt; color: #000000\">&nbsp;</span></o:p></span><span style=\"font-family: \'Arial\',\'sans-serif\'\"><o:p><span style=\"font-size: 12pt; color: #000000\">&nbsp;</span></o:p></span><span style=\"font-family: \'Arial\',\'sans-serif\'\"><o:p></o:p></span>&nbsp; \r\n<p align=\"center\">\r\n<span style=\"font-family: \'Arial\',\'sans-serif\'\"><span style=\"font-size: 12pt\"><span style=\"color: #000000\"><u>Elson J. Passos</u></span></span></span> \r\n</p>\r\n<span style=\"font-family: \'Arial\',\'sans-serif\'\"><span style=\"font-size: 12pt\"><span style=\"color: #000000\"></span></span></span>\r\n<p align=\"justify\">\r\n&nbsp;\r\n</p>\r\n', '', '1', '0', '0', '0', '2008-11-15 15:34:01', '62', '', '2009-04-24 09:49:23', '65', '65', '2009-04-28 15:27:12', '2008-11-15 15:31:18', '0000-00-00 00:00:00', '', '', 'menu_image=-1\nitem_title=1\npageclass_sfx=\nback_button=\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=', '1', '0', '0', '', '', '0', '383');
